import React, { useState } from 'react';
import { deleteResume } from '../API/resumeAPI';
import { useDispatch } from 'react-redux';
import { setResumes } from '../store/reducers/resumeReducer';
import { updatePosts } from '../store/actions/resume';


const Resume = ({
                  id, firstname, lastname, age, gender, email, profile, address, phone, favoriteFilm, salary, about
                }) => {
  const [isEdit, setIsEdit] = useState(false);

  const dispatch = useDispatch()

  const removeResume = async () => {
    await deleteResume(id)
    dispatch(updatePosts())
  }

  return(
    <div className="resume">
      <p>{'Имя: ' +  firstname}</p>
      <p>{'Фамилия: ' +  lastname }</p>
      <p>{'Возраст: ' +  age}</p>
      <p>{'Пол: ' +  gender}</p>
      <p>{'Почта ' +  email}</p>
      <p>{'Направление: ' +  profile}</p>
      <p>{'Адрес: ' +  address.country + ", " + address.city}</p>
      <p>{'Телефон: ' +  phone}</p>
      <p>{'Любимый фильм: ' +  favoriteFilm}</p>
      <p>{'З/п: ' +  salary}</p>
      <p>{'О себе: ' +  about}</p>
      <button>Редактировать</button>
      <button onClick={removeResume}>Удалить</button>
    </div>
  )
}

export default Resume;
